'use strict';

module.exports = function (document, expose) {
	return Object.defineProperties({}, {
		createDocument: { value: function () {
			var doc;
			Object.defineProperties(doc = Object.create(document), {
				parentNode:     { value: null, enumerable: expose },
				ownerDocument:  { value: doc,  enumerable: expose },
				childNodes:     { value: [],   enumerable: expose },
				firstChild:     { value: null, enumerable: expose, configurable: true },
				lastChild:      { value: null, enumerable: expose, configurable: true },
				implementation: { value: this, enumerable: expose }
			});
			doc.appendChild(doc.createNode.apply(doc, arguments));
			return doc;
		}, enumerable: expose }
	});
};
