'use strict';

module.exports = function (t, a) {
	var el, el2, col1, col2, tc, ta;
	tc = t.call; ta = t.apply;

	el = document.createNode();
	col1 = [document.createNode(), document.createNode(), document.createNode()];
	col2 = [document.createNode(), document.createNode(), document.createNode()];

	a(tc(el, col1), el, "Returns self");
	a.deep(el.childNodes, col1, "Replace empty");
	a.deep(tc(el, col2).childNodes, col2, "Replace");

	el2 = tc(document.createNode(), col1);
	a.deep(tc(el, el2.childNodes).childNodes, col1, "Replace from childNodes");

	a.deep(ta(el, col2).childNodes, col2, "Replace from arguments");
};
