'use strict';

module.exports = function (t, a) {
	var doc, root, down, up, outer;

	doc = document;
	root = doc.documentElement;
	down = root.appendChild(doc.createNode()).appendChild(doc.createNode());
	down.name = 'down';
	down.parentNode.name = 'down:parent';
	up = down.appendChild(doc.createNode()).appendChild(doc.createNode());
	up.name = 'up';
	up.parentNode.name = 'up:parent';
	outer = root.appendChild(doc.createNode()).appendChild(doc.createNode());
	outer.name = 'outer';
	outer.parentNode.name = 'outer:parent';

	a.deep(t(up, up), [], "Same");
	a.deep(t(up, up.parentNode), [up.parentNode], "Parent");
	a.deep(t(up, doc), [up.parentNode, down, down.parentNode, root, doc],
		"To doc");
	a.deep(t(doc, up), [root, down.parentNode, down, up.parentNode, up],
		"From doc");

	a.deep(t(down, up), [up.parentNode, up], "Down");
	a.deep(t(up, down), [up.parentNode, down], "Up");

	a.deep(t(up, outer), [up.parentNode, down, down.parentNode, root,
		outer.parentNode, outer], "Outer up");
	a.deep(t(outer, up), [outer.parentNode, root, down.parentNode, down,
		up.parentNode, up], "Outer down");
};
